componentDidMount =  ()=>{
axios.get('/product',{
        headers: {
          'Authorization': `Bearer ${localStorage.getItem("jwt")}`
        }
        }).then((response)=>{
          console.log(response)
          this.list(response.data)
        })}

list = (data)=>{
data.map((value,index)=>{
<div>{value.title}</div>

})
}


    render(){
        return(<div>
            {this.state.loading || this.state.person ? <div>Loading....</div>
