import { render } from '@testing-library/react';

import React from 'react';
import {Link, useHistory} from 'react-router-dom'
import { withRouter } from 'react-router-dom';
import axios from 'axios';


 class Login extends React.Component{
    state = {
    email:'',
    password:"",
    errorMessage:"",
    checking :false,
    }

    handlechange = (event)=>{
        const isCheckbox = event.target.type === "checkbox"
        this.setState({
            [event.target.name]: isCheckbox ?
            event.target.checked
            :event.target.value
        })
    }
    handlesubmit=(event)=>{
        event.preventDefault(); //stops from refreshing the form
        axios.post('/login', {
            email: this.state.email,
            password: this.state.password
          }).then((response) => {   
            this.setState({
                errorMessage:"You are logging in"
            })
            localStorage.setItem("jwt", response.data[0])
            localStorage.setItem("id", response.data[1])
            console.log( 'login')
            console.log( localStorage.getItem('jwt'))
            console.log( localStorage.getItem('id'))
            this.props.history.push('/shop')
            window.location.reload(false);
    
          }, (error) => {
              this.setState({
                  errorMessage:error.response.data
              })
            console.log(error.response.data);
          });
         
    }

    render(){
        return(
            <div>
            <div class="login-container">
  <section class="login" id="login">
    <header>
      <h2>Application Name</h2>
      <h4>Login</h4>
    </header>
    <form class="login-form" action="#" method="post" onSubmit={this.handlesubmit}>
      <input type="text" class="login-input" name = "email"
      onChange ={this.handlechange} placeholder="Enter email" required autofocus/>


      <input type="password"   type = "password"
      value = {this.state.password}
      onChange ={this.handlechange}  class="login-input" placeholder="Password" required/>
      <div class="submit-container">

        <button type="submit" class="login-button">SIGN IN</button>
      </div>
    </form>
  </section>
</div>
<button id="e1">Login error!</button>
</div>

