import React from 'react';
import {Link, useHistory} from 'react-router-dom'
import { withRouter } from 'react-router-dom';
import axios from 'axios';

class Cart extends React.Component{
    state ={
        local : localStorage.getItem("id"),
        items:[]
    }
    
    componentDidMount  =()=>{
        console.log('hi')
        axios.get('/getcart',{
            headers: {
              'Authorization': `Bearer ${localStorage.getItem("jwt")}`
            }}

    ).then((response)=>{
        console.log(response)
        this.setState({
            items:response.data
        })
console.log(response.data)
    })

}
delete =(data)=>{
    //window.location.reload(false);
    console.log("clicked")
    axios.put('/getcart',{
        data:data
    },{
        headers: {
          'Authorization': `Bearer ${localStorage.getItem("jwt")}`
        }}).then((response)=>{
        console.log(response)
        window.location.reload(false);
    })
}
render(){
    return(
      <div> 
      {this.state.items.map((value,index)=>{
        return (
                <div style={{maxWidth:"500px", margin:"auto"}} class="card">
                <div class="card-image waves-effect waves-block waves-light">
                <img class="activator" src={value.URL}/>
                </div>

               <div class="card-content">
  <span class="card-title activator grey-text text-darken-4">{value.title}<i class="material-icons right"></i></span>
  <p><a >${value.price}</a></p>
</div>
<div class="card-reveal">
<span class="card-title grey-text text-darken-4">{value.title}<i class="material-icons right">close</i></span>
<p>{value.description}</p>
</div>

<button class="btn waves-effect waves-light"  onClick={()=>{this.delete(value._id)}} type="submit" name="action">Delete</button>
</div>)}

              ) } </div> )}}


export default withRouter(Cart);
