import { render } from '@testing-library/react';
import React from 'react';
import {Link} from 'react-router-dom'
import axios from 'axios';



class login extends React.Component{
    state = {
     id:'',
    email:'',
    password:"",
    checking :false,
    }

    handlechange = (event)=>{
        const isCheckbox = event.target.type === "checkbox"
        this.setState({
            [event.target.name]: isCheckbox ?
            event.target.checked
            :event.target.value
        })
    }
    handlesubmit=(event)=>{
        event.preventDefault(); //stops from refreshing the form
        axios.post('/login', {
            email: this.state.email,
            password: this.state.password
          }).then((response) => {
            console.log(response);
            console.log('12')
            this.setState({
                id:this.setState(response.data)
            })
          }, (error) => {
            console.log(error.response.data);
          });
    }

    render(){
        return(
            <div className="form">
            <form onSubmit={this.handlesubmit}>
            <div className="form-in"> 
            <h3>Log in</h3>

            <div className="form-group">
                <label>Email</label>
                <input type="email" className="form-control" name = "email"
                 onChange ={this.handlechange} placeholder="Enter email" />
            </div>

            <div className="form-group">
                <label>Password</label>
                <input name = "password"
                type = "password"
                 value = {this.state.password}
                 onChange ={this.handlechange} className="form-control" placeholder="Enter password" />
            </div>


            <button type="submit" className="btn btn-dark btn-lg btn-block">Sign in</button>
            {this.state.errorMessage &&
            <h3 className="error"> { this.state.errorMessage } </h3>}
            </div>
        </form>
        </div>
    );
