import React from 'react';
import {Link, useHistory} from 'react-router-dom'
import { withRouter } from 'react-router-dom';
import axios from 'axios';

class Cart extends React.Component{
    state ={
        local : localStorage.getItem("id"),
        items:[]
    }
    
    componentDidMount  =()=>{
        axios.get('/getcart',{
            headers: {
              'Authorization': `Bearer ${localStorage.getItem("jwt")}`
            }}

    ).then((response)=>{
        console.log(response)
        this.setState({
            items:response.data
        })
console.log(response.data)
    })

}
delete =(data)=>{
    console.log("clicked")
    axios.put('/getcart',{
        data:data
    },{
        headers: {
          'Authorization': `Bearer ${localStorage.getItem("jwt")}`
        }}).then((response)=>{
        console.log(response)
        window.location.reload(false);
    })
}
render(){
    return(
        <div style={{margin:"auto",width:"25%"} }> 
        <div  style={{alignContent:"center"} } class="col s12 m6 ">
          <div style={{height: "auto",width:"450px"} } class="card blue-grey darken-1">
            <div class="card-content white-text">
        <ul class="collection with-header">
        <li class="collection-header teal lighten-2"><h4>Shopping Cart</h4></li>
      {this.state.items.map((value,index)=>{
        return (
            <div class="column">
            <li class="collection-item teal"><div>{value.title} ${value.price}<a class="secondary-content ">
            <a class="waves-effect waves-light btn-small">Button</a>
         </a></div></li>
            </div>
              ) })} </ul> </div> </div>   </div> </div>
             
 )

        }}
export default withRouter(Cart);
