import logo from './logo.svg';
import app from './App.css';
import React from 'react'
import Shop from './components/shop';
import About from './components/about'
import Nav from './components/Nav'
import Login from './components/login'
import Register from './components/registration'
import ItemDetail from './components/componentdetail/itemdetails'
import axios from 'axios';
import { BrowserRouter,Route,Switch,withRouter} from "react-router-dom";

class App extends React.Component {
  state ={
    jwtid:'',
  }

  switching = ()=>{
    this.props.history.push('/shop')
  }
  jwt = (data,place)=> {
    this.setState({
      jwtid:data,
  })
  console.log(this.state.jwtid)
}

  render(){

  return (
    <div className="App">
    <BrowserRouter>
    <Nav/>
    <Switch>
    <Route path ="/login" render = {() =><Login setjwt= {this.jwt} switching={this.switching}/>}/>
    <Route path ="/shop" exact component ={Shop}/>
    <Route path = "/about" exact component = {About}/>
    <Route path = "/register" exact component = {Register}/>
    <Route path = "/shop/:id" exact component = {ItemDetail}/>
    <Route path = "/" render ={()=><div>404 ERROR</div>}/>
    </Switch>
    </BrowserRouter>
    </div>
  );
  }
}

export default App;
