import { render } from '@testing-library/react';

import React from 'react';
import {Link, useHistory} from 'react-router-dom'
import { withRouter } from 'react-router-dom';
import axios from 'axios';


 class Login extends React.Component{
    state = {
    email:'',
    password:"",
    errorMessage:"",
    checking :false,
    }

    handlechange = (event)=>{
        const isCheckbox = event.target.type === "checkbox"
        this.setState({
            [event.target.name]: isCheckbox ?
            event.target.checked
            :event.target.value
        })
    }
    handlesubmit=(event)=>{
        event.preventDefault(); //stops from refreshing the form
        axios.post('/login', {
            email: this.state.email,
            password: this.state.password
          }).then((response) => {   
            this.setState({
                errorMessage:"You are logging in"
            })
            localStorage.setItem("jwt", response.data)
            console.log( 'login')
            console.log( localStorage.getItem('jwt'))
            this.props.history.push('/shop')
          }, (error) => {
              this.setState({
                  errorMessage:error.response.data
              })
            console.log(error.response.data);
          });
    }

    render(){
        return(
            <div className="form">
            <form onSubmit={this.handlesubmit}>
            <div className="form-in">
            <h3><u>Log in</u></h3>

            <div className="form-group">
                <label>Email</label>
                <input type="email" className="form-control" name = "email"
                 onChange ={this.handlechange} placeholder="Enter email" />
            </div>

            <div className="form-group">
                <label>Password</label>
                <input name = "password"
                type = "password"
                 value = {this.state.password}
                 onChange ={this.handlechange} className="form-control" placeholder="Enter password" />
            </div>


            <button type="submit" className="btn btn-dark btn-lg btn-block">Sign in</button>

            {this.state.errorMessage &&
            <div>{ this.state.errorMessage } </div>}
            </div>
        </form>
        </div>
    );
           
    }
}
export default withRouter(Login)
